import React from 'react';
import onlineIcon from '../../icons/onlineIcon.png';
import './TextContainer.css';

const TextContainer = ({ users }) => (
  <div className="textContainer">
    <div>
      <h1>Welcome to this room!<span role="img" aria-label="emoji">üí¨</span></h1>
      <h2>Created with React, Express, Node and Socket.IO <span role="img" aria-label="emoji">‚ù§Ô∏è</span></h2>
      <h2>Try it out right now!!!!!!! <span role="img" aria-label="emoji">‚¨ÖÔ∏è</span></h2>
      <h2>Click the little x on the top of the text box to go to home! </h2>
    </div>
    {
      users
        ? (
          <div>
            <h1>People currently chatting in this room:</h1>
            <div className="activeContainer">
              <h2>
                {users.map(({name}) => (
                  <div key={name} className="activeItem">
                    {name}
                    <img alt="Online Icon" src={onlineIcon}/>
                  </div>
                ))}
              </h2>
            </div>
          </div>
        )
        : null
    }
  </div>
);

export default TextContainer;
